package loopsinjava;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int j=0;
		while(j<5)
		{
		 System.out.println("Hello");
		 j++;
		}

		
		int i=0;
		while(i<=20)
		{
			if(i%2==0)
			{
				System.out.println(i+" is Even");
			}
			else
			{
				System.out.println(i+" is Odd");
			}
			
			i++;
		}
		
	}

}
