package operatorinjava;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=60, m=10;
		
		n+=m;
		System.out.println("Value of n : "+n);
		
		n*=m;
		System.out.println("Value of n : "+n);
		
		n-=m;
		System.out.println("Value of n : "+n);
		
		n/=5;
		System.out.println("Value of n : "+n);
		
		n%=2;
		System.out.println("Value of n : "+n);
		
	}

}
