package operatorinjava;

public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 20, b= 20;
		
		char z = (a != b) ? 'N' : 'Y'; 
		
		System.out.println("Value of Z : "+z);
		
		int x = (a <= b) ? 20 : 30;
		
		System.out.println("Value of x : "+x);
	}

}
