package whileloop;

public class WhileLoopInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int j=1;
		while(j>5)
		{
			System.out.println("Hi");
			j--;
		}
		
		
		System.out.println("*********************");
		int k=6;
		
		do
		{
			System.out.println("Hello");
			k++;
		}while(k<5);
	}

}
