package operatorinjava;

public class ShiftOperatorInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=8;			// 0010 0000
		
		int c = a << 2;
		
		System.out.println("a << 2 = "+c);
		
		c = a >> 2;				// 0000 0010
		
		System.out.println("a >> 2 = "+c);
		
	
	}

}
