package operator;

public class LogicalOperatorInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean a=true;
		boolean b=true;
		
		boolean c = a && b;		//LOGICAL AND
		
		System.out.println("Logical AND : "+c);
		
		c=a || b;				//LOGICAL OR
		
		System.out.println("Logical OR : "+c);
		
	}

}
