package operatorinjava;

public class RelationOperatorInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=25,b=27;
		boolean c; 
		c= a < b;
		System.out.println("a < b ="+c);
		
		c= a > b;
		System.out.println("a > b ="+c);
		
		c= a <= b;
		System.out.println("a <= b ="+c);
		
		c= a >= b;
		System.out.println("a >= b ="+c);
		
		c= a==b;
		System.out.println("a == b ="+c);
		
		c= a!=b;
		System.out.println("a != b ="+c);
	}

}
