package operatorinjava;

public class UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=23;
		
		a++;
		System.out.println(a);
		
		a=25;
		a--;
		System.out.println(a);
		
		++a;
		System.out.println(a);
		
		--a;
		System.out.println(a);
		
		int b;
		a=78;
		
		b=~a;
		System.out.println(b);
		
		boolean x=false,y;
		y=!x;
		
		System.out.println(y);
		
	}

}
