package operatorinjava;

public class UnaryOperator2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=23;
		int b;
		
		b=a++;
			
		System.out.println("a="+a);
		System.out.println("b="+b);
		
		a=75;
		b=++a;
		
		System.out.println("a="+a);
		System.out.println("b="+b);
	}

}
