package second;

public class SecondProgram {

	int x=12;			//class variable .. instance variable
	static int z=34;	//static variable
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count;		//declaration of variable --- Local Variable
		count=100;		//initialization of variable
		
		int a=102;		//declaration and initialization of variable
		
		System.out.println("Value of count = "+a);
		System.out.println("Value of a = "+a);
		
		System.out.println(count);
		
		int d=1,b=2,c=3,e=5,f=7;
		System.out.println("d="+d+" b="+b+" c="+c+" e="+e+" f="+f);
		
	}
}
