package Variable;

public class VariablePgm {
	
	int emp_id;  // instance variables  .... related object of class
	static int emp_acc; //static variable ... related with the class 
	static float n;
	static double d;
	static boolean flag;
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Variable in Java");
		
		int count;	//variable declaration
		count=100;	//variable initilization
		
		count = 150;
		count=650;
		
		System.out.println(count);
		
		System.out.println(emp_acc);
		
		System.out.println(n);
		
		System.out.println(d);

		System.out.println(flag);
		
	}

}
