package loopsinjava;

public class WhileLoopInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=0;
		while(i<5)
		{
			System.out.println("Hello");
			i++;
		}
		
		
		int x[]= {10,20};
		
		int j=0;
		while(j<x.length)
		{
			System.out.println(x[j]);
			j++;
		}
		
	}

}
