package loopsinjava;

public class ForEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double q[]= {45.85,45.25,65.85,21.12};
		
		for(double r:q)
		{
			System.out.println(r);
		}
		
		String str[]= {"Hello","Hi"};
		
		for(String s:str)
		{
			System.out.println(s);
		}
	}

}
