package decisionmakinginjava;

public class DecisionMakingInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=7;
		
		
		System.out.println("Hello We are in Decision Making");
		
		if(a==5)
		{
			a++;
			System.out.println("a is equal");
		}
		
		if(a>13)
		{
			System.out.println("a is greter than 13");
		}
		
		if(a!=2)
		{
			System.out.println("a is not equal to 2");
		}
		System.out.println("Value of a is = "+a);
	}

}
